import React, { useState } from 'react';

const AddEmployeePage = () => {
  const [employee, setEmployee] = useState({
    id: '',
    fullName: '',
    email: '',
    phone: '',
    dateOfBirth: '',
    address: '',
    startDate: '',
    idCardNumber: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setEmployee((prevEmployee) => ({ ...prevEmployee, [name]: value }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    // Do something with the employee data, e.g. send it to a backend API
    console.log(employee);
    // Reset the form
    setEmployee({
      id: '',
      fullName: '',
      email: '',
      phone: '',
      dateOfBirth: '',
      address: '',
      startDate: '',
      idCardNumber: '',
    });
  };

  return (
    <div>
      <h1>Thêm Nhân viên</h1>
      <form onSubmit={handleSubmit}>
        <label>
          ID Nhân viên:
          <input
            type="text"
            name="id"
            value={employee.id}
            onChange={handleChange}
          />
        </label>
        <label>
          Họ tên:
          <input
            type="text"
            name="fullName"
            value={employee.fullName}
            onChange={handleChange}
          />
        </label>
        <label>
          Email:
          <input
            type="email"
            name="email"
            value={employee.email}
            onChange={handleChange}
          />
        </label>
        <label>
          Số điện thoại:
          <input
            type="tel"
            name="phone"
            value={employee.phone}
            onChange={handleChange}
          />
        </label>
        <label>
          Ngày sinh:
          <input
            type="date"
            name="dateOfBirth"
            value={employee.dateOfBirth}
            onChange={handleChange}
          />
        </label>
        <label>
          Địa chỉ:
          <input
            type="text"
            name="address"
            value={employee.address}
            onChange={handleChange}
          />
        </label>
        <label>
          Ngày vào làm:
          <input
            type="date"
            name="startDate"
            value={employee.startDate}
            onChange={handleChange}
          />
        </label>
        <label>
          Số căn cước công dân:
          <input
            type="text"
            name="idCardNumber"
            value={employee.idCardNumber}
            onChange={handleChange}
          />
        </label>
        <button type="submit">Thêm</button>
      </form>
    </div>
  );
};

export default AddEmployeePage;
