import React, { useState } from 'react';
import classNames from 'classnames/bind';
import style from './Staff.module.scss';

export default function Staff() {
  const cx = classNames.bind(style);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [isModalEdit, setIsModalEdit] = useState(false);
  const [email, setEmail] = useState('');
  const [isEmailValid, setIsEmailValid] = useState(true);
  const [staffList, setStaffList] = useState([]);
  const handleIdCardInput = (e) => {
    const value = e.target.value;
    if (value.length > 12) {
      e.target.value = value.slice(0, 12); // Cắt bớt ký tự vượt quá 12
    }
  };
  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  const toggleModalEdit = () => {
    setIsModalEdit(!isModalEdit);
  };

  const handlePhoneNumberInput = (e) => {
    const pattern = /^[0-9]*$/;
    if (!pattern.test(e.key)) {
      e.preventDefault();
    }
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Kiểm tra định dạng email
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const isValid = emailPattern.test(email);

    // Cập nhật trạng thái và hiển thị phản hồi
    setIsEmailValid(isValid);
    if (isValid) {
      // Tiến hành xử lý submit form
      const newStaff = {
        name: document.getElementById('ho_ten').value,
        phone: document.getElementById('sdt_nv').value,
        email: document.getElementById('email').value,
        birthday: document.getElementById('ngaysinh').value,
        address: document.getElementById('diachi').value,
        startDate: document.getElementById('ngayvaolam').value,
        idCard: document.getElementById('cccd').value,
      };

      setStaffList([...staffList, newStaff]);

      // Đặt lại các giá trị form
      document.getElementById('ho_ten').value = '';
      document.getElementById('sdt_nv').value = '';
      setEmail('');
      document.getElementById('ngaysinh').value = '';
      document.getElementById('diachi').value = '';
      document.getElementById('ngayvaolam').value = '';
      document.getElementById('cccd').value = '';

      // Đóng modal
      toggleModal();
    } else {
      // Hiển thị thông báo lỗi
      console.log('Email không đúng định dạng');
    }
  };

  return (
    <div className={cx('wrapper')}>
      <h1>Nhân Viên</h1>
      <div className={cx('tableActions')}>
        <button onClick={toggleModal}>Thêm Nhân Viên</button>
        <button onClick={toggleModalEdit}>Sửa Nhân Viên</button>
        <button>Xóa Nhân Viên</button>
      </div>

      <div className={cx('tableWrapper')}>
        <h2>Danh sách Nhân Viên</h2>

        <table className={cx('table')}>
          <thead>
            <tr>
              <th>ID</th>
              <th>Tên nhân viên</th>
              <th>Email</th>
              <th>SDT</th>
              <th>Ngày sinh</th>
              <th>Địa chỉ</th>
              <th>Ngày vào làm</th>
              <th>Căn cước công dân</th>
            </tr>
          </thead>
          <tbody>
            {staffList.map((staff, index) => (
              <tr key={index}>
                <td>{index + 1}</td>
                <td>{staff.name}</td>
                <td>{staff.email}</td>
                <td>{staff.phone}</td>
                <td>{staff.birthday}</td>
                <td>{staff.address}</td>
                <td>{staff.startDate}</td>
                <td>{staff.idCard}</td>
              </tr>
            ))}
          </tbody>
        </table>
      </div>

      {isModalOpen && (
        <div className={cx('modal')}>
          <div className={cx('modalContent')}>
            <h3>Thêm Nhân Viên</h3>
            <form onSubmit={handleSubmit}>
              <div>
                <label htmlFor="ho_ten">Tên:</label>
                <input type="text" id="ho_ten" maxLength={30} placeholder="Nhập tên..." required />
              </div>

              <div>
                <label htmlFor="sdt_nv">Số điện thoại:</label>
                <input
                  maxLength={10}
                  type="tel"
                  id="sdt_nv"
                  placeholder="Nhập số điện thoại..."
                  pattern="[0-9]{10}"
                  onKeyPress={handlePhoneNumberInput}
                  required
                />
              </div>

              <div>
                <label htmlFor="email">Email:</label>
                <input
                  type="email"
                  id="email"
                  placeholder="Nhập email..."
                  value={email}
                  onChange={handleEmailChange}
                  className={!isEmailValid ? 'invalid' : ''}
                  required
                />
                {!isEmailValid && <p className="error">Email không đúng định dạng</p>}
              </div>

              <div>
                <label htmlFor="ngaysinh">Ngày sinh:</label>
                <input type="date" id="ngaysinh" placeholder="Nhập ngày sinh..." required />
              </div>

              <div>
                <label htmlFor="diachi">Địa chỉ:</label>
                <input type="text" id="diachi" placeholder="Nhập địa chỉ" required />
              </div>

              <div>
                <label htmlFor="ngayvaolam">Ngày vào làm:</label>
                <input type="date" id="ngayvaolam" placeholder="Ngày vào làm..." required />
              </div>

              <div>
                <label htmlFor="cccd">Căn cước công dân:</label>
                <input type="text" id="cccd" maxLength={30} placeholder="Nhập căn cước công dân..." required />
              </div>
              {/* Thêm các trường khác */}
              <div className={cx('add')}>
                <button type="submit" className={cx('addButton')}>Thêm</button>
                <button type="button" className={cx('cancelButton')} onClick={toggleModal}>Hủy</button>
              </div>
            </form>
          </div>
        </div>
      )}

      {isModalEdit && (
        <div className={cx('modal')}>
          <div className={cx('modalContent')}>
            <h3>Sửa Nhân Viên</h3>
            <form>
              <div>
                <label htmlFor="ho_ten">Tên:</label>
                <input type="text" id="ho_ten" maxLength={30} placeholder="Nhập tên..." required />
              </div>

              <div>
                <label htmlFor="sdt_nv">Số điện thoại:</label>
                <input
                  maxLength={10}
                  type="tel"
                  id="sdt_nv"
                  placeholder="Nhập số điện thoại..."
                  pattern="[0-9]{10}"
                  onKeyPress={handlePhoneNumberInput}
                  required
                />
              </div>

              <div>
                <label htmlFor="email">Email:</label>
                <input
                  type="email"
                  id="email"
                  placeholder="Nhập email..."
                  value={email}
                  onChange={handleEmailChange}
                  className={!isEmailValid ? 'invalid' : ''}
                  required
                />
                {!isEmailValid && <p className="error">Email không đúng định dạng</p>}
              </div>

              <div>
                <label htmlFor="ngaysinh">Ngày sinh:</label>
                <input type="date" id="ngaysinh" placeholder="Nhập ngày sinh..." required />
              </div>

              <div>
                <label htmlFor="diachi">Địa chỉ:</label>
                <input type="text" id="diachi" placeholder="Nhập địa chỉ" required />
              </div>

              <div>
                <label htmlFor="ngayvaolam">Ngày vào làm:</label>
                <input type="date" id="ngayvaolam" placeholder="Ngày vào làm..." required />
              </div>

              <div>
  <label htmlFor="cccd">Căn cước công dân:</label>
  <input type="text" id="cccd" maxLength={12} placeholder="Nhập căn cước công dân..." required onInput={handleIdCardInput} />
</div>

              {/* Thêm các trường khác */}
              <div className={cx('add')}>
                <button type="submit" className={cx('addButton')}>Lưu</button>
                <button type="button" className={cx('cancelButton')} onClick={toggleModalEdit}>Hủy</button>
              </div>
            </form>
          </div>
        </div>
      )}
    </div>
  );
}
