import React, { useState } from 'react';
import classNames from 'classnames/bind';
import style from './Staff.module.scss';

export default function Staff() {
  const cx = classNames.bind(style);
  const [isModalOpen, setIsModalOpen] = useState(false);
  const [email, setEmail] = useState('');
  const [isEmailValid, setIsEmailValid] = useState(true);

  const toggleModal = () => {
    setIsModalOpen(!isModalOpen);
  };

  const handlePhoneNumberInput = (e) => {
    const pattern = /^[0-9]*$/;
    if (!pattern.test(e.key)) {
      e.preventDefault();
    }
  };

  const handleEmailChange = (e) => {
    setEmail(e.target.value);
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Kiểm tra định dạng email
    const emailPattern = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
    const isValid = emailPattern.test(email);

    // Cập nhật trạng thái và hiển thị phản hồi
    setIsEmailValid(isValid);
    if (isValid) {
      // Tiến hành xử lý submit form
      // ...
    } else {
      // Hiển thị thông báo lỗi
      console.log('Email không đúng định dạng');
    }
  };

  return (
    <div className={cx('wrapper')}>
      <h1>Nhân Viên</h1>
      <div className={cx('tableActions')}>
        <button onClick={toggleModal}>Thêm Nhân Viên</button>
        <button>Sửa Nhân Viên</button>
        <button>Xóa Nhân Viên</button>
      </div>

      <div className={cx('tableWrapper')}>
        <h2>Danh sách Nhân Viên</h2>

        <table className={cx('table')}>
          <thead>
            <tr>
              <th>ID</th>
              <th>Tên nhân viên</th>
              <th>Email</th>
              <th>SDT</th>
              <th>Ngày sinh</th>
              <th>Địa chỉ</th>
              <th>Ngày vào làm</th>
              <th>Căn cước công dân</th>
            </tr>
          </thead>
          <tbody>
            <tr>
              <td>1</td>
              <td>Nguyễn Văn A</td>
              <td>Sản phẩm A</td>
              <td>0333333333</td>
              <td>01/01/2001</td>
              <td>Ninh Kiều, Cần Thơ</td>
              <td>1/7/2023</td>
              <td>36405070113</td>
            </tr>
            <tr>
              <td>2</td>
              <td>Nguyễn Văn A</td>
              <td>Sản phẩm A</td>
              <td>0333333333</td>
              <td>01/01/2001</td>
              <td>Ninh Kiều, Cần Thơ</td>
              <td>1/7/2023</td>
              <td>36405070113</td>
            </tr>
          </tbody>
        </table>
      </div>

      {isModalOpen && (
        <div className={cx('modal')}>
          <div className={cx('modalContent')}>
            <h3>Thêm Nhân Viên</h3>
            <form>
              <div>
                <label htmlFor="ho_ten">Tên:</label>
                <input type="text" id="ho_ten" maxLength={30} placeholder="Nhập tên..." />
              </div>

              <div>
                <label htmlFor="sdt_nv">Số điện thoại:</label>
                <input
                  maxLength={10}
                  type="tel"
                  id="sdt_nv"
                  placeholder="Nhập số điện thoại..."
                  pattern="[0-9]{10}"
                  onKeyPress={handlePhoneNumberInput}
                />
              </div>

              <div>
                <label htmlFor="email">Email:</label>
                <input
                  type="email"
                  id="email"
                  placeholder="Nhập email..."
                  value={email}
                  onChange={handleEmailChange}
                  className={!isEmailValid ? 'invalid' : ''}
                />
                {!isEmailValid && <p className="error">Email không đúng định dạng</p>}
              </div>

              <div>
                <label htmlFor="ngaysinh">Ngày sinh:</label>
                <input type="date" id="ngaysinh" placeholder="Nhập ngày sinh..." />
              </div>

              <div>
                <label htmlFor="diachi">Địa chỉ:</label>
                <input type="text" id="diachi" placeholder="Nhập địa chỉ" />
              </div>

              <div>
                <label htmlFor="ngayvaolam">Ngày vào làm:</label>
                <input type="date" id="ngayvaolam" placeholder="Ngày vào làm..." />
              </div>

              <div>
                <label htmlFor="cccd">Căn cước công dân:</label>
                <input type="text" id="cccd" maxLength={30} placeholder="Nhập căn cước công dân..." />
              </div>
              {/* Thêm các trường khác */}
            </form>
            <div className={cx('add')}>
              <button type="button" onClick={toggleModal}>Hủy</button>
            </div>
            <div className={cx('delete')}>
              <button type="button" onClick={toggleModal}>Hủy</button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};
